export default {data:{QbXnaU6myG:{data:{id:195,attributes:{imdbId:"tt2543164",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BMTExMzU0ODcxNDheQTJeQWpwZ15BbWU4MDE1OTI4MzAy._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"7.9\u002F10"},{Source:"Rotten Tomatoes",Value:"94%"},{Source:"Metacritic",Value:"81\u002F100"}],item_lang:{data:{id:858,attributes:{title:"Arrival",plot:"Linguistics professor Louise Banks leads an elite team of investigators when gigantic spaceships touchdown in 12 locations around the world. As nations teeter on the verge of global war, Banks and her crew must race against time to find a way to communicate with the extraterrestrial visitors. Hoping to unravel the mystery, she takes a chance that could threaten her life and quite possibly all of mankind.",slug:"arrival"}}},books:{data:[{id:490,attributes:{title:"Stories of Your Life and Others",description:"An award-winning book from the author of Exhalation, this short story collection \"blend s] absorbing storytelling with meditations on the universe, being, time and space. . . . raises questions about the nature of reality and what it is to be human.\" --The New York Times Includes \"Story of Your Life\" the basis for the major motion picture Arrival Stories of Your Life and Others delivers dual delights of the very, very strange and the heartbreakingly familiar, often presenting characters who must confront sudden change--the inevitable rise of automatons or the appearance of aliens--with some sense of normalcy. With sharp intelligence and humor, Chiang examines what it means to be alive in a world marked by uncertainty, but also by beauty and wonder. An award-winning collection from one of today's most lauded writers, Stories of Your Life and Others is a contemporary classic.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=XIRKDAAAQBAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:["Ted Chiang"],firstBook:true,published:"2016-01-01",publisher:"Vintage",subtitle:""}}]}}},meta:{}}},prerenderedAt:1680940915104}