export default (function(a,b,c,d,e,f,g,h){return {data:{d4WAbXU86P:{data:{id:1957,attributes:{imdbId:"tt0053137",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BMTMyNTIzMDEyMV5BMl5BanBnXkFtZTcwODQwOTc2NA@@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"7.2\u002F10"},{Source:"Rotten Tomatoes",Value:"77%"},{Source:"Metacritic",Value:"55\u002F100"}],item_lang:{data:{id:2620,attributes:{title:b,plot:"In 1964, atomic war wipes out humanity in the northern hemisphere; one American submarine finds temporary safe haven in Australia, where life-as-usual covers growing despair. In denial about the loss of his wife and children in the holocaust, American Captain Towers meets careworn but gorgeous Moira Davidson, who begins to fall for him. The sub returns after reconnaissance a month (or less) before the end; will Towers and Moira find comfort with each other?",slug:"on-the-beach"}}},books:{data:[{id:9829,attributes:{title:b,description:e,thumbnail:f,authors:[c],firstBook:true,published:g,publisher:h,subtitle:a}},{id:9830,attributes:{title:b,description:"On the Beach is a 1957 post-apocalyptic novel written by British-Australian author Nevil Shute after he emigrated to Australia. The novel details the experiences of a mixed group of people in Melbourne as they await the arrival of deadly radiation spreading towards them from the northern hemisphere following a nuclear war a year previously. As the radiation approaches each person deals with their impending death in different ways. [Suggest a different description.]",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=3PqKswEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:["Nevil Shute Norway, Nevil Shute"],firstBook:d,published:"2018-01-17",publisher:"Createspace Independent Publishing Platform",subtitle:a}},{id:9831,attributes:{title:b,description:a,thumbnail:a,authors:[c],firstBook:d,published:"1977-01-01",publisher:a,subtitle:c}},{id:9832,attributes:{title:"On the Beach (Nevil Shute Norway) (Literary Thoughts Edition)",description:"Literary Thoughts edition presents On the Beach by Nevil Shute Norway ------ \"On the Beach\", written in 1957 by British-Australian author Nevil Shute Norway (Nevil Shute Norway (1899-1960), is a post-apocalyptic novel, which details the experiences of a mixed group of people in Melbourne after a preceding nuclear war and the subsequent arrival of deadly radiation ... All books of the Literary Thoughts edition have been transscribed from original prints and edited for better reading experience. Please visit our homepage www.literarythoughts.com to see our other publications.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=P-94DQAAQBAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:["Nevil Shute Norway"],firstBook:d,published:"2016-11-08",publisher:"epubli",subtitle:a}},{id:9833,attributes:{title:b,description:e,thumbnail:f,authors:[c],firstBook:d,published:g,publisher:h,subtitle:a}}]}}},meta:{}}},prerenderedAt:1680940950591}}("","On the Beach","Nevil Shute",null,"\"The most shocking fiction I have read in years. What is shocking about it is both the idea and the sheer imaginative brilliance with which Mr. Shute brings it off.\" THE SAN FRANCISCO CHRONICLE They are the last generation, the innocent victims of an accidental war, living out their last days, making do with what they have, hoping for a miracle. As the deadly rain moves ever closer, the world as we know it winds toward an inevitable end....","https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=p24G5ZeTfJUC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api","2010-02-09","Vintage"))