export default (function(a,b,c,d,e,f){return {data:{"3SA9yCbUhA":{data:{id:2212,attributes:{imdbId:"tt0056632",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BYzkzYTQzYTItMzhhYy00YzNiLWEzMDUtYmNlMWU1MTVhZDFjXkEyXkFqcGdeQXVyNjY1NTQ0NDg@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.7\u002F10"},{Source:"Rotten Tomatoes",Value:"67%"}],item_lang:{data:{id:2875,attributes:{title:b,plot:"An intelligent, articulate scholar, Harrison MacWhite, survives a hostile Senate confirmation hearing at the hands of conservatives to become ambassador to Sarkan, a southeast Asian country where civil war threatens a tense peace. Despite his knowledge, once he's there, MacWhite sees only a dichotomy between the U.S. and Communism. He can't accept that anti-American sentiment might be a longing for self-determination and nationalism. So, he breaks from his friend Deong, a local opposition leader, ignores a foreman's advice about slowing the building of a road, and tries to muscle ahead. What price must the country and his friends pay for him to get some sense?",slug:"the-ugly-american"}}},books:{data:[{id:10838,attributes:{title:b,description:"First published in 1958, The Ugly American became a runaway national bestseller for its slashing expose of American arrogance, incompetence, and corruption in Southeast Asia. Based on fact, the book's eye-opening stories and sketches drew a devastating picture of how the United States was losing the struggle with Communism in Asia. Combining gripping storytelling with an urgent call to action, the book prompted President Eisenhower to launch a study of our military aid program that led the way to much-needed reform.\"Powerful and absorbing.... Should be required reading in Washington\". -- Kirkus Reviews\"Not only important but consistently entertaining.... The attack on American policy in Asia this book makes is clothed in sharp characterizations, frequently humorous incident, and perceptive descriptions of the countries and people where the action occurs\". -- Robert Trumbull, former chief correspondent for the New York Times in China and Southeast Asia",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=rYuTGQpZlD0C&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[d,e],firstBook:true,published:f,publisher:"Fawcett Books",subtitle:a}},{id:10839,attributes:{title:b,description:a,thumbnail:a,authors:[d],firstBook:c,published:"1965-01-01",publisher:a,subtitle:"By William J. Lederer and Eugene Burdick"}},{id:10840,attributes:{title:b,description:a,thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=ZH3hEC9K54cC&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:["William J. Lederer, Eugene Burdick"],firstBook:c,published:f,publisher:a,subtitle:a}},{id:10841,attributes:{title:"Teacher's Guide, The Ugly American by William J. Lederer and Eugene Burdick",description:a,thumbnail:a,authors:["Jacqueline Parker",e],firstBook:c,published:"1000-01-01",publisher:a,subtitle:a}},{id:10842,attributes:{title:"The Deceptive American",description:"In 1965, the authors of The Ugly American published the novel Sarkhan, a book which they felt had an even more dramatic message than their great best-seller. Sarkhan was greeted with high praise, was a selection of both the Literary Guild and the Reader s Digest Condensed Book Club.\"",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=EZnrIAAACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[d,e],firstBook:c,published:"1977-11-01",publisher:"W. W. Norton",subtitle:a}}]}}},meta:{}}},prerenderedAt:1680940935400}}("","The Ugly American",null,"William J. Lederer","Eugene Burdick","1958-01-01"))