export default (function(a,b,c,d,e,f,g,h,i,j){return {data:{yCuFyZmbCn:{data:{id:906,attributes:{imdbId:"tt0085461",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BYzc5NmIxNjItN2NjZC00Yzc2LWJjZDQtMjM2OWQwZDIzZmI2XkEyXkFqcGdeQXVyNjMwMjk0MTQ@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"7.6\u002F10"},{Source:"Rotten Tomatoes",Value:"100%"}],item_lang:{data:{id:1569,attributes:{title:d,plot:"In a touring Shakespearean theater group, a backstage hand, the dresser, Norman (Sir Tom Courtenay), is devoted to the brilliant but tyrannical head of the company. He struggles to support deteriorating star Sir (Albert Finney) as the company struggles to carry on during the London blitz. The pathos of his backstage efforts rival the pathos in the story of Lear and the Fool that is being presented on-stage, as the situation comes to a crisis.",slug:"the-dresser"}}},books:{data:[{id:5447,attributes:{title:d,description:a,thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=WLIIAQAAIAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[b],firstBook:true,published:"1980-01-01",publisher:"Grove Press",subtitle:a}},{id:5448,attributes:{title:e,description:f,thumbnail:g,authors:[b],firstBook:c,published:h,publisher:i,subtitle:j}},{id:5449,attributes:{title:"An English Tragedy",description:"May 1945: victory in Europe and a Labour landslide in the UK. English traitor John Amery is arrested in Italy and brought back to London for trial. If convicted, he faces the death penalty. But his father is a senior politician. Surely the Establishment will look after its own?Based on an extraordinary true story, An English Tragedy is a disturbing tale of wartime treason and conflicting loyalties by Ronald Harwood, author of the Oscarr winning screenplay for The Pianist, and stage plays including The Dresser, Taking Sides and Quartet.An English Tragedy premiered at Watford Palace Theatre in February, 2008.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=jE4qAQAAIAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[b],firstBook:c,published:"2008-01-01",publisher:a,subtitle:a}},{id:5450,attributes:{title:e,description:f,thumbnail:g,authors:[b],firstBook:c,published:h,publisher:i,subtitle:j}},{id:5451,attributes:{title:d,description:a,thumbnail:a,authors:["Theatre Aquarius Archives (University of Guelph)",b,"Masao Tanaka","Peter Mandia"],firstBook:c,published:"1992-01-01",publisher:a,subtitle:a}}]}}},meta:{}}},prerenderedAt:1680940959772}}("","Ronald Harwood",null,"The Dresser","Ronald Harwood's Adaptations","This is an in-depth analysis and discussion of the craft of writing for the cinema by Oscar winning screenwriter Ronald Harwood. As well as exploring the numerous elements that the screenwriter has to deal with, the text analyses four specific screenplays in depth, from concept to final film.","https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=vXwcAQAAIAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api","2007-01-01","Guerilla Books","From Other Works Into Films"))