export default (function(a,b,c,d){return {data:{jCsJbN6OI7:{data:{id:1056,attributes:{imdbId:"tt0106868",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BYmQ1ZjE1YTMtMDE0NC00OTk4LWFlZDEtNWNiODE2NDg2YjdjXkEyXkFqcGdeQXVyNjMwMjk0MTQ@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.6\u002F10"},{Source:"Rotten Tomatoes",Value:"45%"}],item_lang:{data:{id:1719,attributes:{title:"A Far Off Place",plot:"The witty Nonni and the stuck-up city-boy Harry are the only ones to survive a massacre of a gang of poachers among the gamekeeper's family on his lonesome farm in the savanna. Now the ruthless murderers are after them as the only witnesses. Without a means of transportation, the only way to escape is to walk through two thousand kilometers of Kalahari desert with the help of the African bushman Xhabbo. On the months-long journey ahead, they not only become good friends against their differences, but also realize that every one of them has strength and skills that are required to survive.",slug:"a-far-off-place"}}},books:{data:[{id:6094,attributes:{title:"A Far-off Place",description:"The story of a long, perilous journey undertaken by four survivors of a massacre: a teenage boy of European descent, a young white girl, and two Bushmen.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=xYNbAAAAMAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:["Laurens Van der Post"],firstBook:true,published:"1974-01-01",publisher:"William Morrow",subtitle:c}},{id:6095,attributes:{title:"The Lost World of the Kalahari",description:"Laurens van der Post was fascinated and appalled at the fate of this remarkable people. Ostracised by all the changing face of African cultural life they retreated deep into the Kalahari desert. His fascinating attempt to capture their way of life and the secrets of their ancient heritage provide captivating reading and a unique insight into a forgotten way of life.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=NNLBi3C9UOIC&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:b,published:"2010-05-25",publisher:d,subtitle:"With 'The Great and the Little Memory'"}},{id:6096,attributes:{title:"Feather Fall",description:"This Van Der Post 'reader', thematically organised to reflect the patterns and themes which have influenced his life and his writing, distils the essence of the writer, thinker, spiritual guru and man of action. This evocative and thought-provoking selection, combining short paragraphs and longer passages is chosen with love and insight from his published and some unpublished writings, (books, introductions, lectures, essays, ) and will give pleasure and inspiration to generations of readers the world over.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=OexJAAAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:b,published:"2013-11-30",publisher:d,subtitle:"An Anthology of Laurens Van Der Post"}},{id:6097,attributes:{title:"Yet Being Someone Other",description:"Yet Being Someone Other is the most revealing book that Laurens van der Post wrote about his extraordinary and eventful life, and the most far-reaching; it is a distillation of the experiences that have moved him at the deepest level of the imagination and made him the exceptional person and writer he was.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=OoV1fHB2wIkC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:b,published:"2011-08-31",publisher:d,subtitle:c}},{id:6098,attributes:{title:"The Heart of the Hunter",description:"In this stirring sequel to The Lost World of the Kalahari Laurens van der Post records everything he has learned of the life and lore of Africa’s first inhabitants. He explores the very sources of the Bushmen’s spirit and imagination - their dreams and stories, the legends that guide them and inspire them in their daily battles with that harshest of environments, the Kalahari.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=hsjyAAAAMAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:b,published:"2002-01-01",publisher:"Arrow",subtitle:c}}]}}},meta:{}}},prerenderedAt:1680940967206}}("Laurens Van Der Post",null,"","Random House"))