export default (function(a,b,c,d,e,f,g,h,i){return {data:{gsdor91D3h:{data:{id:1065,attributes:{imdbId:"tt0068575",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BYjQyYzQ2ZWEtZWUwZi00MDVjLTk4MzYtYTc2NmJmYTY5YzBhL2ltYWdlL2ltYWdlXkEyXkFqcGdeQXVyNjc1NTYyMjg@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"7.3\u002F10"},{Source:"Rotten Tomatoes",Value:"100%"},{Source:"Metacritic",Value:"88\u002F100"}],item_lang:{data:{id:1728,attributes:{title:b,plot:"The film tells the story of two boxers and their problems. One of them is on the decline of his career while the other one just begins his ascent in this sport.",slug:"fat-city"}}},books:{data:[{id:6135,attributes:{title:b,description:e,thumbnail:f,authors:[c],firstBook:true,published:g,publisher:h,subtitle:a}},{id:6136,attributes:{title:b,description:e,thumbnail:f,authors:[c],firstBook:d,published:g,publisher:h,subtitle:a}},{id:6137,attributes:{title:b,description:a,thumbnail:a,authors:[c],firstBook:d,published:i,publisher:a,subtitle:a}},{id:6138,attributes:{title:b,description:"»Fat City hat mich mehr bewegt als die gesamte Gegenwartsliteratur der letzten Jahre.« Joan Didion Stockton, Kalifornien, 50er Jahre. Im Lido-Box-Gym treffen Ernie Munger und Billy Tully aufeinander. Ernie steht am Anfang seiner Karriere, ist voller Hoffnungen. Billy ist am Ende, will es noch einmal wissen. Ihr Trainer, Ruben Luna, hat schon alles gesehen, er weiß, dass es im Boxen kaum etwas zu holen gibt. Zwischen verlausten Hotelbetten und Pfirsichernte, zwischen Boxring und dunklen Bars, der großen Liebe und der leidlichen Bettgesellschaft, zwischen Hoffnung und Verzweiflung beginnt und vergeht das Leben von Ernie, Billy und Ruben. »Fat City« zählt zu den großen Klassikern der amerikanischen Literatur, der Roman ist das Gegenstück zu Rocky und Co. Keine Heldengeschichte, sondern eine Verbeugung vor dem letzten Willen, der erst erwacht, wenn alle Hoffnung unter den Tisch getrunken ist. Eine Liebeserklärung an eine Zeit, in der man von der Hand in den Mund lebte, aus eigener Kraft alles erreichen und alles zerstören konnte. Gregor Hens gelingt es mit seiner Neuübersetzung meisterhaft, dem einzigartigen Californian-Working-Class-Sound, dem trotzigen Humor und der feinen Melancholie eine deutsche Stimme zu verleihen. »Laufen, kämpfen, lieben, Unkraut jäten oder Obsternte – die Protagonisten des Romans halten niemals inne, nur um nicht von ihrem Verhängnis eingeholt zu werden. Gardner erzählt das so überzeugend, dass wir uns nur an ihre Taten erinnern, nicht an ihre Niederlagen.« The New York Review of Books",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=W7qADQAAQBAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[c],firstBook:d,published:i,publisher:"Aufbau Digital",subtitle:"Roman"}},{id:6139,attributes:{title:"Fat city",description:a,thumbnail:a,authors:[c,"Juan Antonio Molina Foix"],firstBook:d,published:"1988-01-01",publisher:a,subtitle:a}}]}}},meta:{}}},prerenderedAt:1680940967033}}("","Fat City","Leonard Gardner",null,"A story of small time boxing in Stockton, California in the 1950s.","https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=4j9SwKjghzMC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api","1996-10-06","Univ of California Press","2017-04-11"))