export default (function(a,b,c,d,e){return {data:{BeDavdgsMl:{data:{id:1145,attributes:{imdbId:"tt6861902",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BYTgwYzFiYWEtMTNmNC00ODg2LWE0ZjUtNzgyNDk2Y2VhYjFhXkEyXkFqcGdeQXVyNTQ1MzA2NTk@._V1_SX300.jpg",type:"movie",ratings:[],item_lang:{data:{id:1808,attributes:{title:"Foreigner",plot:"N\u002FA",slug:"foreigner"}}},books:{data:[{id:6452,attributes:{title:d,description:"When a terrorist bomb kills his wife and daughter in a London department store, former Viet Cong guerilla Nguyen Ngoc obeys his code of honor and sets out to exact revenge for the murders. By the author of Pay Off. National ad\u002Fpromo.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=8iQyksn0LrgC&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:true,published:"1992-01-01",publisher:"Beyond Words\u002FAtria Books",subtitle:b}},{id:6453,attributes:{title:d,description:"**Now a major motion picture as THE FOREIGNER starring Pierce Brosnan and Jackie Chan.** Stephen Leather's BESTSELLING breakthrough thriller. The Chinaman understood death. Jungle-skilled, silent and lethal, he had killed for the Viet Cong and then for the Americans. He had watched helpless when his two eldest daughters had been raped and killed by Thai pirates. Now all that was behind him. Quiet, hard-working and unassuming, he was building up his South London take-away business. Until the day his wife and youngest daughter were destroyed by an IRA bomb in a Knightsbridge department store. Then, simply but persistently, he began to ask the authorities who were the men responsible, what was being done. And was turned away, fobbed off, treated as a nuisance. Which was when the Chinaman, denied justice, decided on revenge. And went back to war.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=aiBEMQAACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:c,published:"2017-09-12",publisher:"Hodder",subtitle:"Now filmed as The Foreigner"}},{id:6454,attributes:{title:"The Three Mike Cramer Novels",description:"Three page-turning action thrillers from Stephen Leather: THE CHINAMAN, THE DOUBLE TAP and THE LONG SHOT.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=xp4dBoN9EBoC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:c,published:"2013-03-21",publisher:"Hachette UK",subtitle:"The Chinaman, The Long Shot, The Double Tap"}},{id:6455,attributes:{title:"Chinaman",description:"The Chinaman understood death. Jungle-skilled, silent and lethal, he had killed for the Viet Cong and then for the Americans. He had watched helpless when his two eldest daughters had been raped and killed by Thai pirates. Now all that was behind him. Quiet, hard-working and unassuming, he was building up his South London take-away business. Until the day his wife and youngest daughter were destroyed by an IRA bomb in a Knightsbridge department store. Then, simply but persistently, he began to ask the authorities who were the men responsible, what was being done. And was turned away, fobbed off, treated as a nuisance. Which was when the Chinaman, denied justice, decided on revenge. And went back to war.",thumbnail:b,authors:[a],firstBook:c,published:"1999-05-20",publisher:e,subtitle:b}},{id:6456,attributes:{title:d,description:"HUNGRY GHOST Geoff Howells, a government-trained killer, is sent to Hong Kong to assassinate Chinese Mafia leader Simon Ng. Howells devises a complicated plan to reach his intended victim - only to find himself the next target . . . Patrick Dugan, a lowly Hong Kong policeman, has dangerous connections - his sister is married to Ng. But when Ng and his daughter disappear, Dugan gets caught up in an international spying intrigue that is running violently out of control . . . THE CHINAMAN Silent and lethal, he had killed for the Viet Cong and then for the Americans, but years later, building up a take-away business in London, all that was behind him. Until his wife and youngest daughter were killed by an IRA bomb. Then, simply but persistently, he began to ask the authorities what was being done. And was fobbed off, treated as a nuisance. Which was when the Chinaman, denied justice, decided on revenge.",thumbnail:b,authors:[a],firstBook:c,published:"2004-01-05",publisher:e,subtitle:"Hungry Ghost"}}]}}},meta:{}}},prerenderedAt:1680940972553}}("Stephen Leather","",null,"The Chinaman","Coronet"))