export default (function(a,b,c,d,e){return {data:{GtCTYCGmO3:{data:{id:1254,attributes:{imdbId:"tt0077617",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BNTFmMTM0ODMtYzVlMS00YWViLThiYjEtMWRjNjM5ZjA5NTY2XkEyXkFqcGdeQXVyMjI4MjA5MzA@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.6\u002F10"},{Source:"Rotten Tomatoes",Value:"60%"}],item_lang:{data:{id:1917,attributes:{title:"Go Tell the Spartans",plot:"A unit of American military advisors in Vietnam prior to the major U.S. involvement find similarities between their helpless struggle against the Viet Cong and the doomed actions of a French unit at the same site a decade before in this bitter look at the beginnings of the Vietnam war.",slug:"go-tell-the-spartans"}}},books:{data:[{id:6912,attributes:{title:d,description:"\"In April 1964, Daniel Ford opened his mail to find a check for $1,250-- an advance against royalties on Now Comes Theodora, his first novel, which Doubleday published the following year. Meanwhile the money bought a ticket to Saigon, where he spent three months with American advisors, helicopter crews, Seals, and Special Forces teams on their rounds of South Vietnam. There were 25,000 American military personnel and only fifty reporters in the country that summer. Almost all were volunteers, very few bothered with body armor, and nobody wore a steel helmet. The experience inspired Incident at Muc Wa and the Burt Lancaster film Go Tell the Spartans. 'Sad, bawdy, and compelling, ' wrote the reviewer from the Detroit Free Press.\"--Page 4 of cover.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=7BybMQEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:true,published:"2012-08-20",publisher:"Createspace Independent Publishing Platform",subtitle:e}},{id:6913,attributes:{title:d,description:"This is the novel that inspired the acclaimed Burt Lancaster movie, Go Tell the Spartans. The year is 1964--early days in South Vietnam--and the U.S. Army Raiders have garrisoned a town that the French abandoned ten years before. The Viet Cong attack, and the Americans reinforce. They're not about to repeat the mistakes of the French! 'Sad, bawdy, and compelling,' wrote the Detroit Free Press. Prophetic, too, of how the larger war would end.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=_3RsDwAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:b,published:"2018-07-02",publisher:c,subtitle:e}},{id:6914,attributes:{title:"Cowboy",description:"Cowboy was handsome, flamboyant, courageous, clever, and cruel. He got his nickname from the Green Berets who worked with him in the Highlands of South Vietnam in the 1960s. \"You've got to take the bad with the good,\" one Special Forces captain said of him. \"And Cowboy is a good interpreter.\" And in the end, he was murdered by his own side, the Montagnard rebels who were equally opposed to the Communists in Hanoi and the generals in Saigon. A compelling look at a country and a people caught up in a Cold War they couldn't understand, and which in the end destroyed them.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=notVDwAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:b,published:"2018-05-01",publisher:c,subtitle:"The Interpreter Who Became a Soldier, a Warlord, and One More Casualty of Our War in Vietnam"}},{id:6915,attributes:{title:"Now Comes Theodora",description:"\"A richly colorful novel,\" said the New York Times, but in the author's home town, the bookstore manager kept it under the counter and sold it only to those who dared ask for it. Woodstock was still four years in the future, and American students were more concerned about nuclear weapons than a war in Vietnam. But already there was something blowing in the wind, as the young Daniel Ford was among the first to document in his debut novel. \"It is impossible not to keep on watching them,\" said The New Yorker of his heroine and his anti-heroes, and the Los Angeles Times agreed: \"An effervescent eye-opener.\"",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=78R2DwAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:b,published:"1965-01-01",publisher:c,subtitle:"A Story of the 1960s"}},{id:6916,attributes:{title:"The Only War We've Got",description:"",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=udQuDwAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:b,published:"2017-07-31",publisher:c,subtitle:"Early Days in South Vietnam"}}]}}},meta:{}}},prerenderedAt:1680940978557}}("Daniel Ford",null,"Warbird Books","Incident at Muc Wa","A Story of the Vietnam War"))