export default (function(a,b,c,d){return {data:{yXtgwm5ooD:{data:{id:1335,attributes:{imdbId:"tt0099703",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BMjNiYzg3OTUtYTQ2Mi00OTAwLWI4OGMtZGNkNDhmZDU4ZGRmXkEyXkFqcGdeQXVyNDkzNTM2ODg@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.9\u002F10"},{Source:"Rotten Tomatoes",Value:"91%"},{Source:"Metacritic",Value:"86\u002F100"}],item_lang:{data:{id:1998,attributes:{title:b,plot:"Lily works for a bookie, placing bets to change the odds at the track. When her son is hospitalized after an unsuccessful con job and resultant beating, she finds that even an absentee parent has feelings for her child. This causes her own job to go wrong as well. Each of them faces the down side of the grift.",slug:"the-grifters"}}},books:{data:[{id:7237,attributes:{title:b,description:"To his friends, to his coworkers, and even to his mistress Moira, Roy Dillon is an honest hardworking salesman. He lives in a cheap hotel just within his pay bracket. He goes to work every day. He has hundreds of friends and associates who could attest to his good character. Yet, hidden behind three gaudy clown paintings in Roy's pallid hotel room, sits fifty-two thousand dollars--the money Roy makes from his short cons, his \"grifting.\" For years, Roy has effortlessly maintained control over his house-of-cards life--until the simplest con goes wrong, and he finds himself critically injured and at the mercy of the most dangerous woman he ever met: his own mother. THE GRIFTERS, one of the best novels ever written about the art of the con, is an ingeniously crafted story of deception and betrayal that was the basis for Stephen Frears' and Martin Scorsese's critically-acclaimed film of the same name.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=UsQNnwEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[c],firstBook:true,published:"2014-08-05",publisher:"Mulholland Books",subtitle:a}},{id:7238,attributes:{title:b,description:"Jim Thompson's classic The Grifters is one of the best novels ever written about the art of the con, an ingeniously crafted story of deception and betrayal that was the basis for the critically acclaimed film by Stephen Frears and Martin Scorcese. To his friends, to his coworkers, and even to his mistress Moira, Roy Dillon is an honest hardworking salesman. He lives in a cheap hotel just within his pay bracket. He goes to work every day. He has hundreds of friends and associates who could attest to his good character. Yet, hidden behind three gaudy clown paintings in Roy's pallid hotel room, sits fifty-two thousand dollars — the money Roy makes from his short cons, his \"grifting.\" For years, Roy has effortlessly maintained control over his house-of-cards life — until the simplest con goes wrong, and he finds himself critically injured and at the mercy of the most dangerous woman he ever met: his own mother.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=hI5cDwAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[c],firstBook:d,published:"2011-11-01",publisher:"Hachette UK",subtitle:a}},{id:7239,attributes:{title:"Grifters",description:a,thumbnail:a,authors:[c],firstBook:d,published:"1991-01-01",publisher:a,subtitle:a}},{id:7240,attributes:{title:b,description:a,thumbnail:a,authors:["Donald E. Westlake"],firstBook:d,published:"1989-01-01",publisher:a,subtitle:a}},{id:7241,attributes:{title:"The Getaway",description:a,thumbnail:a,authors:[c],firstBook:d,published:"1983-01-01",publisher:a,subtitle:"With Killer Inside Me, Grifters and Pop"}}]}}},meta:{}}},prerenderedAt:1680940982618}}("","The Grifters","Jim Thompson",null))