export default (function(a,b,c,d,e,f,g){return {data:{p6ZwDfucyw:{data:{id:1396,attributes:{imdbId:"tt0412808",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BNGFmMTI0NDUtYmJhMC00ZDgwLWJmMjUtM2EyMjFiYWU2YTNlXkEyXkFqcGdeQXVyMTY5Nzc4MDY@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.0\u002F10"},{Source:"Rotten Tomatoes",Value:"30%"},{Source:"Metacritic",Value:"48\u002F100"}],item_lang:{data:{id:2059,attributes:{title:"The Hawk Is Dying",plot:"George Gatling (Paul Giamatti) works as an upholsterer, but his true calling is the ancient art of falconry. He shares that passion with his beloved nephew, Fred (Michael Pitt), but their previous attempts to train a wild bird have all met with failure. The two capture a magnificent red-tailed hawk, but then a horrible tragedy devastates George. Utterly grief-stricken, he refuses to eat or sleep until he wins the hawk's trust.",slug:"the-hawk-is-dying"}}},books:{data:[{id:1042,attributes:{title:"Classic Crews",description:"Includes two of Crews' full-length novels, The Gypsy's Curse and Car, his autobiography, and three of his essays.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=AC2hEV-FGJEC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:true,published:"1993-10-08",publisher:"Simon and Schuster",subtitle:"A Harry Crews Reader"}},{id:1043,attributes:{title:"The Hawk is Dying",description:b,thumbnail:b,authors:[a],firstBook:c,published:"1974-01-01",publisher:"Harvill Secker",subtitle:b}},{id:1044,attributes:{title:d,description:"The highly acclaimed memoir of one of the most original American storytellers of the rural South A Penguin Classic Harry Crews grew up as the son of a sharecropper in Georgia at a time when “the rest of the country was just beginning to feel the real hurt of the Great Depression but it had been living in Bacon County for years.” Yet what he conveys in this moving, brutal autobiography of his first six years of life is an elegiac sense of community and roots from a rural South that had rarely been represented in this way. Interweaving his own memories including his bout with polio and a fascination with the Sears, Roebuck catalog, with the tales of relatives and friends, he re-creates a childhood of tenderness and violence, comedy and tragedy.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=8Hg3EAAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:c,published:e,publisher:f,subtitle:g}},{id:1045,attributes:{title:"The Gospel Singer",description:"“Harry Crews is magnificently twisted and brutally funny.” - Carl Hiaasen A Penguin Classic Golden-haired, with the voice of an angel and a reputation as a healer, the Gospel Singer appeared on the cover of LIFE and brought thousands to their knees in Carnegie Hall. But for all his fame, he is a man in mortal torment that drives him back to his obscure and wretched hometown of Enigma, Georgia. But by the time his Cadillac pulls into Enigma, he discovers an old friend is being held at tenuous bay from a lynch mob. As Harry Crews’s first novel unfolds, the Gospel Singer is forced to give way to his torment, and in doing so he reveals to the believers who have gathered at his feet just how little he is God’s man, and how much he has contributed to the corruption of each of them.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=8ng3EAAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:c,published:e,publisher:f,subtitle:b}},{id:1046,attributes:{title:d,description:"Harry Crew recounts his childhood, focusing on the people, places, and circumstances that shaped him into the author he is today.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=GxROLxIJM7cC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:c,published:"1995-01-01",publisher:"University of Georgia Press",subtitle:g}}]}}},meta:{}}},prerenderedAt:1680940980917}}("Harry Crews","",null,"A Childhood","2022-03-15","Penguin","The Biography of a Place"))