export default (function(a,b,c,d,e,f,g){return {data:{QTpyQRk1Jv:{data:{id:1560,attributes:{imdbId:"tt0065872",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BNjdmMTI5NWMtOTYzZC00ZmU5LTlmMDAtN2NkYWJiM2ZmYzk0XkEyXkFqcGdeQXVyMTMxMTY0OTQ@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"7.5\u002F10"},{Source:"Rotten Tomatoes",Value:"100%"}],item_lang:{data:{id:2223,attributes:{title:b,plot:"Hackman plays a New York professor who wants a change in his life, and plans to get married to his girlfriend and move to California. His mother understands his need to get away, but warns him that moving so far away could be hard on his father. Just before the wedding, the mother dies. Hackman's sister (who has been disowned by their father for marrying a Jewish man) advises him to live his own life, and not let himself be controlled by their father.",slug:"i-never-sang-for-my-father"}}},books:{data:[{id:8175,attributes:{title:b,description:e,thumbnail:f,authors:[c],firstBook:true,published:g,publisher:a,subtitle:a}},{id:8176,attributes:{title:b,description:e,thumbnail:f,authors:[c],firstBook:d,published:g,publisher:a,subtitle:a}},{id:8177,attributes:{title:"What If Someday I Could Make a Movie",description:"Robert Geller's What If Someday I Could Make A Movie puts to rest the tired clich√© about adaptations of books-\"which was better, the film or the book?\" As a teacher of literature and screenwriting he cites his motivation for attempting the impossible. His memoir details the struggles he faced in bringing America's finest writers to the screen. Without apology, he affirms his passionate affinity for the written words that shaped his teaching career and his loving fascination for movies that helped to shape his life.From intuition to script, to the daily production struggles, his memoir describes the challenges confronted while bringing Baldwin, Bellow, Hemingway, O'Connor and Updike to large viewing audiences nationwide, hoping to acquaint readers with masterful storytellers.Geller's commitment to a personal dream enabled him to gather an unmatched array of talent-Teresa Wright, Robin Williams, Blythe Danner, Jerry Stiller, Tommy Lee Jones and Ron Howard who joined with him to create twenty-four critically acclaimed films.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=HEGgFmzjOpsC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:["Robert Geller"],firstBook:d,published:"2005-11-01",publisher:"iUniverse",subtitle:"A Memoir"}},{id:8178,attributes:{title:"Elements of Literature",description:a,thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=0orZAAAAMAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[c],firstBook:d,published:"1993-01-01",publisher:"Holt Rinehart & Winston",subtitle:"Fourth Course"}},{id:8179,attributes:{title:b,description:a,thumbnail:a,authors:[c],firstBook:d,published:"1968-01-01",publisher:a,subtitle:"A Play in Two Acts"}}]}}},meta:{}}},prerenderedAt:1680940970659}}("","I Never Sang for My Father","Robert Anderson",null,"\"A man who wants to move on with his life by moving to California and marry his girlfriend, finds it difficult as he still lives in the towering shadow of his aging father.\"-- Internet Movie Database.","https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=58YrAQAAIAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api","1970-01-01"))