export default (function(a,b,c,d){return {data:{"1AgrwyIrd4":{data:{id:1954,attributes:{imdbId:"tt1667321",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BMzQ1MjI5OTg1M15BMl5BanBnXkFtZTgwMzIyODc4NDM@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.3\u002F10"},{Source:"Rotten Tomatoes",Value:"68%"},{Source:"Metacritic",Value:"62\u002F100"}],item_lang:{data:{id:2617,attributes:{title:c,plot:"Adapted by Ian McEwan from his bestselling novel, the drama centers on a young couple of drastically different backgrounds in the summer of 1962. Following the pair through their idyllic courtship, this movie explores sex and the societal pressure that can accompany physical intimacy, leading to an awkward and fateful wedding night.",slug:"on-chesil-beach"}}},books:{data:[{id:9814,attributes:{title:c,description:"\"It is July 1962. Edward and Florence, young innocents married that morning, arrive at a hotel on the Dorset coast. At dinner in their rooms they struggle to suppress their private fears of the wedding night to come...\" - Back cover.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=9geU3z7_XBMC&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[b],firstBook:true,published:"2008-01-01",publisher:"Arrow",subtitle:a}},{id:9815,attributes:{title:"On Chesil Beach (Movie Tie-In Edition)",description:"The official movie tie-in edition of Ian McEwan's exquisite, bestselling novel: the basis for the major motion picture starring Saoirse Ronan, Emily Watson, Anne-Marie Duff, and Samuel West. England, 1962: Florence and Edward are celebrating their wedding in a hotel on the Dorset coast. Yet as they dine, the expectation of their marital duties become overwhelming. Unbeknownst to them both, the decisions they make this night will resonate throughout their lives. With exquisite prose, Ian McEwan creates a story of lives transformed by a gesture not made or a word not spoken--and brilliantly illuminates the collision of sexual longing, deep-seated fears, and romantic fantasy on a young couple's wedding night.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=thQ3tAEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[b],firstBook:d,published:"2018-05-08",publisher:"Vintage Books Canada",subtitle:a}},{id:9816,attributes:{title:"Chesil Beach",description:a,thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=Vrv-d9CEQZYC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[b],firstBook:d,published:"2007-01-01",publisher:"Giulio Einaudi Editore",subtitle:a}},{id:9817,attributes:{title:c,description:"NATIONAL BESTSELLER • The Booker Prize-winning author of Atonement brilliantly illuminates the collision of sexual longing, deep-seated fears, and romantic fantasy on a young couple’s wedding night. It is 1962, and Florence and Edward are celebrating their wedding in a hotel on the Dorset coast. Yet as they dine, the expectation of their marital duties become overwhelming. Unbeknownst to them both, the decisions they make this night will resonate throughout their lives. With exquisite prose, Ian McEwan creates in On Chesil Beach a story of lives transformed by a gesture not made or a word not spoken.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=C2-peRGa_oIC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[b],firstBook:d,published:"2008-06-10",publisher:"Anchor",subtitle:a}},{id:9818,attributes:{title:c,description:a,thumbnail:a,authors:[b],firstBook:d,published:"2007-09-01",publisher:a,subtitle:a}}]}}},meta:{}}},prerenderedAt:1680940950642}}("","Ian McEwan","On Chesil Beach",null))