export default (function(a,b,c,d,e,f,g,h){return {data:{CD9ouU7pgf:{data:{id:2133,attributes:{imdbId:"tt0082934",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BZGE2OWVhYzgtMTRmYS00NjI0LTg3MGItZDlkN2Q3ZTQ5MzgxXkEyXkFqcGdeQXVyNjUwNzk3NDc@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.6\u002F10"},{Source:"Rotten Tomatoes",Value:"79%"},{Source:"Metacritic",Value:"61\u002F100"}],item_lang:{data:{id:2796,attributes:{title:a,plot:"This remake of the 1946 movie of the same name accounts an affair between a seedy drifter and a seductive wife of a roadside café owner. This begins a chain of events that culminates in murder.",slug:"the-postman-always-rings-twice"}}},books:{data:[{id:10538,attributes:{title:a,description:e,thumbnail:f,authors:[c],firstBook:true,published:g,publisher:h,subtitle:b}},{id:10539,attributes:{title:a,description:"This collection features three classics from the master of the noir novel--each of which was made into a classic Hollywood film--along with five otherwise unavailable short stories. Ribbon marker.",thumbnail:b,authors:[c],firstBook:d,published:"1989-01-01",publisher:b,subtitle:b}},{id:10540,attributes:{title:a,description:e,thumbnail:f,authors:[c],firstBook:d,published:g,publisher:h,subtitle:b}},{id:10541,attributes:{title:a,description:"Three classics from the noir master include signature pieces about murder, obsessive love, and psychological devotion; and are complemented by five short works including \"Pastorale,\" \"The Baby in the Icebox,\" \"Dead Man,\" \"Brush Fire,\" and \"The Girl in the Storm.\" 15,000 first printing.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=4U9aAAAAMAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:["James Mallahan Cain"],firstBook:d,published:"2003-01-01",publisher:"Everyman's Library",subtitle:"Double Indemnity ; Mildred Pierce ; and Selected Stories"}},{id:10542,attributes:{title:"Dead Man",description:"In this powerful tale of guilt, a short story from James M. Cain’s The Postman Always Rings Twice, Double Indemnity, Mildred Pierce, and Selected Stories, a desperate man is driven to the edge of madness after he accidentally kills a railroad detective. As the murderer works to get his alibi straight, he soon discovers that the prick of his own conscience is just as oppressive as the long arm of the law. A Vintage Short.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=bcUzEAAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[c],firstBook:d,published:"2021-10-26",publisher:"Vintage",subtitle:"From The Postman Always Rings Twice, Double Indemnity, Mildred Pierce, and Selected Stories"}}]}}},meta:{}}},prerenderedAt:1680940939797}}("The Postman Always Rings Twice","","James M. Cain",null,"An amoral young tramp. A beautiful, sullen woman with an inconvenient husband. A problem that has only one grisly solution--a solution that only creates other problems that no one can ever solve. First published in 1934 and banned in Boston for its explosive mixture of violence and eroticism, The Postman Always Rings Twice is a classic of the roman noir. It established James M. Cain as a major novelist with an unsparing vision of America's bleak underside, and was acknowledged by Albert Camus as the model for The Stranger.","https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=ATG99XjemiUC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api","2010-11-03","Vintage Crime\u002FBlack Lizard"))