export default (function(a,b,c,d){return {data:{ZXwvsvtet3:{data:{id:218,attributes:{imdbId:"tt0055774",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BNjhiMWM5MzgtNTdkMC00ZTFmLWEzMjEtZmZkOTY2MmQwNzhjXkEyXkFqcGdeQXVyNjc1NTYyMjg@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.9\u002F10"},{Source:"Rotten Tomatoes",Value:"89%"}],item_lang:{data:{id:881,attributes:{title:b,plot:"In times of great upheaval during Passover in early-first-century Jerusalem, the fifth governor of the Roman province of Judaea, Pontius Pilate, finds himself before a pressing dilemma. As part of a tradition, the indecisive ruler offers the agitated crowd the choice to have either Jesus of Nazareth or the murderer, Barabbas, released from Roman custody; but, instead, the people demand the release of the thief. Now, as Jesus takes Barabbas' place on the cross, an inhumane act of punishment paves the way for an arduous spiritual journey of faith, leading Barabbas to Sicily's dark sulphur mines, and the blood-soaked soil of Emperor Nero's Coliseum. Will Jesus' sacrifice set Barabbas, the slave, free?",slug:"barabbas"}}},books:{data:[{id:2616,attributes:{title:b,description:"The condemned thief in whose place Jesus was crucified is tormented by guilt and struggles to understand the meaning of the dead rabbi's teachings",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=BwGYtwEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[c],firstBook:true,published:"1989-01-01",publisher:"Vintage",subtitle:a}},{id:2617,attributes:{title:b,description:"Barabbas apparaît dans les évangiles comme un personnage trouble du ter siècle après J.-C. Résistant à l'oppression romaine, ou simple bandit, il sera jugé en même temps que le Christ, et quand Ponce Pilate demande au peuple de choisir entre ces deux hommes, c'est Barabbas que le peuple graciera. Le roman de Pär Lagerkvist restitue la vie mouvementée et la conscience inquiète de Barabbas après qu'il a vu exécuté celui que les premiers Chrétiens annoncent comme leur Sauveur. Il est tout d'abord sceptique : le fils de Dieu lui-même se serait laissé exécuter ainsi ? Pour sauver l'humanité ? Mais Barabbas ne sera plus le même après avoir assisté à ce moment historique, la crucifixion de Jésus sur le mont Golgotha. A travers ce destin, Pär Lagerkvist plonge le lecteur dans la période de formation du christianisme. Il en dépeint l'intensité et le caractère déterminant.",thumbnail:a,authors:[c,"Diane de Margerie"],firstBook:d,published:"2008-01-01",publisher:a,subtitle:"roman"}},{id:2618,attributes:{title:b,description:a,thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=Id7ZAAAAMAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[c],firstBook:d,published:"1962-01-01",publisher:a,subtitle:a}},{id:2619,attributes:{title:b,description:"Om Barabbas' liv efter frigivelsen",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=ZapJAAAAMAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[c],firstBook:d,published:"1950-01-01",publisher:a,subtitle:"Roman"}},{id:2620,attributes:{title:b,description:a,thumbnail:a,authors:["Emery Bekessy","Pär Fabian Lagerkvist"],firstBook:d,published:"1983-08-01",publisher:"Rivercity Press",subtitle:a}}]}}},meta:{}}},prerenderedAt:1680940917001}}("","Barabbas","Pär Lagerkvist",null))