export default (function(a,b,c,d,e){return {data:{J1neguu5qE:{data:{id:2451,attributes:{imdbId:"tt0053453",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BNWY0YmYyNzAtMzY2Zi00ZTlmLTgwNmQtMjcwMTc5NjMzYzE0XkEyXkFqcGdeQXVyMjUxODE0MDY@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.2\u002F10"}],item_lang:{data:{id:3114,attributes:{title:c,plot:"Having fled to Mexico from the U.S. many years ago for killing his father's murderer, Martin Brady travels to Texas to broker an arms deal for his Mexican boss, strongman Governor Cipriano Castro. Brady breaks a leg and while recuperating in Texas the gun shipment is stolen. Complicating matters further the wife of local army major Colton has designs on him, and the local Texas Ranger captain makes him a generous offer to come back to the states and join his outfit. After killing a man in self defense, Brady slips back over the border and confronts Castro who is not only unhappy that Brady has lost his gun shipment but is about to join forces with Colton to battle the local raiding Apache Indians.",slug:"the-wonderful-country"}}},books:{data:[{id:11848,attributes:{title:c,description:"Originally published in 1952, Tom Lea’s The Wonderful Country opens as mejicano pistolero Martín Bredi is returning to El Puerto (El Paso) after a fourteen-year absence. Bredi carries a gun for the Chihuahuan warlord Cipriano Castro and is on Castro’s business in Texas. Fourteen years earlier—shortly after the end of the Civil War—when he was the boy Martin Brady, he killed the man who murdered his father and fled to Mexico where he became Martín Bredi. Back in Texas Brady breaks a leg; then he falls in love with a married woman while recuperating; and, finally, to right another wrong, he kills a man. When Brady\u002FBredi returns to Mexico, the Castros distrust him as an American. He becomes a man without a country. The Wonderful Country clearly depicts life along the Texas-Mexico border of a century-and-a-half ago, when Texas and Mexico were being settled and tamed.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=RA0qDwAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[b],firstBook:true,published:"2017-06-28",publisher:"Pickle Partners Publishing",subtitle:a}},{id:11849,attributes:{title:"The Wonderful Country, a Novel by Tom Lea; with Drawings by the Author",description:a,thumbnail:a,authors:[b],firstBook:d,published:e,publisher:a,subtitle:a}},{id:11850,attributes:{title:"The Brave Bulls",description:"One of Texas's true renaissance men, Tom Lea (1907-2001) was already a noted artist, muralist, and book illustrator when he published his first novel, The Brave Bulls, in 1949. This suspenseful story of bullfighting in Mexico, elegantly illustrated by the author, spent several weeks on the New York Times bestseller list and was hailed by Time magazine as the best first novel of the year. It also won the Carr P. Collins Award from the Texas Institute of Letters, went through numerous reprints and translations, and became a 1951 movie starring Mel Ferrer and Anthony Quinn.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=YB96EKbwQcEC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[b],firstBook:d,published:"2002-05-15",publisher:"University of Texas Press",subtitle:a}},{id:11851,attributes:{title:c,description:"Tom Lea's The Wonderful Country opens as mejicano pistolero Martín Bredi is returning to El Puerto [El Paso] after a fourteen-year absence. Bredi carries a gun for the Chihuahuan warlord Cipriano Castro and is on Castro's business in Texas. Fourteen years earlier--shortly after the end of the Civil War--when he was the boy Martin Brady, he killed the man who murdered his father and fled to Mexico where he became Martín Bredi. Back in Texas Brady breaks a leg; then he falls in love with a married woman while recuperating; and, finally, to right another wrong, he kills a man. When Brady\u002FBredi returns to Mexico, the Castros distrust him as an American. He becomes a man without a country. The Wonderful Country clearly depicts life along the Texas-Mexico border of a century-and-a-half ago, when Texas and Mexico were being settled and tamed.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=rDXu4TjryAMC&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[b],firstBook:d,published:"2002-01-01",publisher:"TCU Press",subtitle:a}},{id:11852,attributes:{title:c,description:a,thumbnail:a,authors:[b],firstBook:d,published:e,publisher:a,subtitle:"A Novel"}}]}}},meta:{}}},prerenderedAt:1680940919700}}("","Tom Lea","The Wonderful Country",null,"1952-01-01"))