export default (function(a,b,c,d,e){return {data:{HJGd64Zryt:{data:{id:2524,attributes:{imdbId:"tt0120271",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BMTI1MTI5NTk4OF5BMl5BanBnXkFtZTcwNTI4NDUyMQ@@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.0\u002F10"}],item_lang:{data:{id:3187,attributes:{title:d,plot:"This is the story of a young Irish woman who comes to Spain to escape from the pressures she feels about her impending marriage to a political activist in Ireland. But in Spain in the 1930's, taking a job of governess in a wealthy family, she finds the same kinds of political unrest. In fact, it isn't long before she finds herself attracted to a married man who is similarly involved in the struggle against fascism and Franco. This awakens her to her nature that brings her to such men and resolves for her what she must do about the life she left in Ireland.",slug:"talk-of-angels"}}},books:{data:[{id:12144,attributes:{title:d,description:"Reissued to coincide with the release of the movie version, a controversial novel, banned in Ireland in 1936 for its frank depiction of lesbianism, follows an Irish woman who becomes involved with a fellow expatriate in Spain. Movie tie-in.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=Cc5uPQAACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[b],firstBook:true,published:"1997-08-08",publisher:"Miramax Books",subtitle:a}},{id:12145,attributes:{title:d,description:a,thumbnail:a,authors:[b],firstBook:c,published:"1997-01-01",publisher:a,subtitle:"A Novel"}},{id:12146,attributes:{title:"Mary Lavelle Talk of Angels",description:"Mary Lavelle, a young Irish woman, journeys to Spain, which is fast approaching civil war, to work as a governess. There Mary soon finds her convent education and beliefs challenged by the fiery politics and passionate opinions of the family's son Juanito. Finding themselves at the heart of a family and a nation divided, Mary and Juanito seize the opportunity to consummate their love in a night of passion and betrayal that foretells the coming of a new era.",thumbnail:a,authors:[b],firstBook:c,published:"1997-07-01",publisher:"Virago Press",subtitle:a}},{id:12147,attributes:{title:e,description:a,thumbnail:a,authors:[b],firstBook:c,published:"1999-09-01",publisher:a,subtitle:"Talk of Angels B Pbp"}},{id:12148,attributes:{title:e,description:"Mary Lavelle, a beautiful young Irish woman, travels to Spain to see some of the world before marrying her steadfast fiance John. But despite the enchanting surroundings and her three charming charges, life as governess to the wealthy Areavaga family is lonely and she is homesick. Then comes the arrival of the family's handsome, passionate - and married - son Juanito and Mary's loyalties and beliefs are challenged. Falling in love with Juanito and with Spain, Mary finds herself at the heart of a family and a nation divided.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=GeTNCwAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[b],firstBook:c,published:"2016-05-19",publisher:"Hachette UK",subtitle:a}}]}}},meta:{}}},prerenderedAt:1680940914633}}("","Kate O'Brien",null,"Talk of Angels","Mary Lavelle"))