export default (function(a,b,c,d,e){return {data:{hGwHtNJTUF:{data:{id:284,attributes:{imdbId:"tt3691740",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BNjAzOTUzNTY3Ml5BMl5BanBnXkFtZTgwMjYwNzE5ODE@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.4\u002F10"},{Source:"Rotten Tomatoes",Value:"74%"},{Source:"Metacritic",Value:"66\u002F100"}],item_lang:{data:{id:947,attributes:{title:d,plot:"Ten-year-old Sophie is in for the adventure of a lifetime when she meets the Big Friendly Giant. Naturally scared at first, the young girl soon realizes that the twenty-four-foot behemoth is actually quite gentle and charming. As their friendship grows, Sophie's presence attracts the unwanted attention of Bloodbottler, Fleshlumpeater, and other giants. After travelling to London, Sophie and the BFG must convince Queen Elizabeth to help them get rid of all of the bad giants once and for all.",slug:"the-bfg"}}},books:{data:[{id:2934,attributes:{title:"The BFG (Colour Edition)",description:"The much-loved Roald Dahl story now in full colour format. When Sophie is snatched from her bed in the middle of the night by a giant with a stride as long as a tennis court she is sure she's going to be eaten for breakfast. But luckily for Sophie, the BFG is far more jumbly than his disgusting neighbours, whose favourite pastime is guzzling up whoppsy-whiffling human beans. Sophie is determined to stop all this, and so she and the BFG cook up an ingenious plan to rid of the world of the Bloodbottler, the Fleshlumpeater and all their rotsome friends forever.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=XJH7DAAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[a],firstBook:true,published:"2016-09-13",publisher:"Penguin UK",subtitle:b}},{id:2935,attributes:{title:d,description:"The BFG--Big Friendly Giant--kidnaps Sophie from her bed in the orphanage and takes her back to Giantland where she becomes involved in a scheme to end the loathsome activities of nine evil giants.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=DgFyngEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:c,published:"2007-08-01",publisher:e,subtitle:b}},{id:2936,attributes:{title:"The Bfg",description:"With notes on staging, props, and costumes, a collection of plays based on the author's book can be performed with the minimum of experience or resources. Reprint.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=Bc6QPwAACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:c,published:"2008-10-01",publisher:b,subtitle:"A Set of Plays"}},{id:2937,attributes:{title:d,description:"Sophie is snatched from her bed by a Big Friendly Giant who carries her away to Giant Country where she has a marvellous adventure.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=r4kAoQEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a,"Natasha Richardson"],firstBook:c,published:"2007-01-01",publisher:e,subtitle:b}},{id:2938,attributes:{title:"Creative Writing with the BFG",description:"Bring your story alive in a whoppsy setting! Explore adjectives, imagery, similes and more with the BFG. These fun activities and writing tasks help to develop language and vocabulary skills, giving you the tools you need to write your own story. Learn how to set a scene, appeal to the senses and write detailed descriptions of your setting.Roald Dahl's Creative Writing sparks creativity, builds confidence and inspires young writers through the wonderful worlds of these best-loved stories. Filled with top tips and ideas boxes, each book introduces techniques and methods to help you plan and write a phizz-whizzing story of your own!",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=en_huQEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:["Ladybird Staff"],firstBook:c,published:"2019-01-24",publisher:"Ladybird",subtitle:"How to Write Splendid Settings"}}]}}},meta:{}}},prerenderedAt:1680940922147}}("Roald Dahl","",null,"The BFG","Puffin"))