export default (function(a,b,c,d,e){return {data:{QgrUqNPFu7:{data:{id:705,attributes:{imdbId:"tt0115964",poster:"https:\u002F\u002Fm.media-amazon.com\u002Fimages\u002FM\u002FMV5BZjlhMDBlNDQtMjZkZi00NzJmLWI4MTgtZDdkMzZmZDcyZGNmXkEyXkFqcGdeQXVyMTQxNzMzNDI@._V1_SX300.jpg",type:"movie",ratings:[{Source:"Internet Movie Database",Value:"6.4\u002F10"},{Source:"Rotten Tomatoes",Value:"63%"},{Source:"Metacritic",Value:"50\u002F100"}],item_lang:{data:{id:1368,attributes:{title:a,plot:"Since a road accident left him with serious facial and bodily scarring, a former TV scientist has become obsessed by the marriage of motor-car technology with what he sees as the raw sexuality of car-crash victims. The scientist, along with a crash victim he has recently befriended, sets about performing a series of sexual acts in a variety of motor vehicles, either with other crash victims or with prostitutes whom they contort into the shape of trapped corpses. Ultimately, the scientist craves a suicidal union of blood, semen, and engine coolant, a union with which he becomes dangerously obsessed.",slug:"crash"}}},books:{data:[{id:4616,attributes:{title:a,description:"Two young men become obsessed with sexual fantasies involving automobiles, accidents, injuries, and wounds, in a new edition of the underground classic. Reprint.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=Y8-iHi1IBigC&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[b],firstBook:true,published:"2001-10-05",publisher:"Macmillan",subtitle:d}},{id:4617,attributes:{title:a,description:"The Definitive Cult, Postmodern Novel—a Shocking Blend of Violence, Transgression, and Eroticism Reissued with a New Introduction from Zadie Smith When J. G. Ballard, our narrator, smashes his car into another and watches a man die in front of him, he finds himself drawn with increasing intensity to the mangled impacts of car crashes. Robert Vaughan, a former TV scientist turned nightmare angel of the expressway, has gathered around him a collection of alienated crash victims and experiments with a series of autoerotic atrocities, each more sinister than the last. But Vaughan craves the ultimate crash—a head-on collision of blood, semen, engine coolant, and iconic celebrity. First published in 1973, Crash remains one of the most shocking novels of the twentieth century and was made into an equally controversial film by David Cronenberg.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=xr06DwAAQBAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[b],firstBook:c,published:"2017-12-05",publisher:"Picador USA",subtitle:d}},{id:4618,attributes:{title:a,description:"Robert Vaughan, a former TV scientist turned nightmare angel of the expressway, has gathered a collection of alienated crash victims and experiments with a series of autoerotic atrocities, each more sinister than the last. But Vaughan craves the ultimate crash--a head-on collision of blood, semen, engine coolant, and iconic celebrity. First published in 1973, Crash remains one of the most shocking novels of the 20th century.tury.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=R6mmuAEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[b],firstBook:c,published:"2019-07-23",publisher:"Rare Bird Books, a Vireo Book",subtitle:"Deluxe Edition"}},{id:4619,attributes:{title:a,description:"David Cronenberg's Crash brought a storm of controversy when it was first screened in London. It has almost none of the explicit violence of the J.G. Ballard novel from which it was adapted. This book explores the relationship between the novel and film.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=V31ZAAAAMAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:["Iain Sinclair"],firstBook:c,published:"1999-04-01",publisher:"British Film Inst",subtitle:e}},{id:4620,attributes:{title:"The Best Short Stories of J. G. Ballard",description:"First published in 1978, this collection of nineteen of J. G. Ballard's best short stories is as timely and informed as ever. His tales of the human psyche and its relationship to nature and technology, as viewed through a strong microscope, were eerily prescient and now provide greater perspective on our computer-dominated culture. Ballard's voice and vision have long served as a font of inspiration for today's cyber-punks, the authors and futurists who brought the information age into the mainstream.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=7WjfAwAAQBAJ&printsec=frontcover&img=1&zoom=5&edge=curl&source=gbs_api",authors:[b],firstBook:c,published:"2014-07-15",publisher:"Holt Paperbacks",subtitle:e}}]}}},meta:{}}},prerenderedAt:1680940948427}}("Crash","J. G. Ballard",null,"A Novel",""))