export default (function(a,b,c,d){return {data:{weOwdVc3HH:{data:{id:912,attributes:{imdbId:"tt4094192",poster:"N\u002FA",type:"movie",ratings:[],item_lang:{data:{id:1575,attributes:{title:"Drop",plot:"Drop in and swim along.",slug:"drop"}}},books:{data:[{id:5476,attributes:{title:c,description:"Bob Saginowski finds himself at the centre of a robbery gone awry and entwined in an investigation that digs deep into the neighborhood's past where friends, families, and foes all work together to make a living - no matter the cost.",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=wXf4oAEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:true,published:"2014-08-19",publisher:b,subtitle:b}},{id:5477,attributes:{title:c,description:"New York Times bestselling author Dennis Lehane's The Drop is a crime story, a love story, and ultimately a journey of faith, set in Boston's underworld. \"[A] gritty gem...a stark and moving short novel.\" -- Publishers Weekly Two days after Christmas, Bob Saginowski, a lonely bartender looking for a reason to live, rescues an abused and abandoned pit bull puppy from a trash can and meets a damaged woman named Nadia Dunn looking for something to believe in. As their relationship grows, they cross paths with the Chechen mafia, who have taken over his cousin Marv's bar and use it as a money drop; Marv himself, who has grown dangerously desperate with age and thwarted hopes; two hapless stick-up artists who make the mistake of robbing the bar; a very curious cop; and the original owner of the puppy, a psycho named Eric Deeds, who wants his dog back. . .",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=cSxUzgEACAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:d,published:"2021-12-14",publisher:"William Morrow Paperbacks",subtitle:b}},{id:5478,attributes:{title:"Coronado",description:"Presents a compilation of some of the author's previously published short works, in a volume that includes an original short tale and the story, \"Until Gwen.\"",thumbnail:"https:\u002F\u002Fbooks.google.com\u002Fbooks\u002Fcontent?id=JOplAAAAMAAJ&printsec=frontcover&img=1&zoom=5&source=gbs_api",authors:[a],firstBook:d,published:"2006-08-08",publisher:"William Morrow",subtitle:"Stories"}}]}}},meta:{}}},prerenderedAt:1680940959667}}("Dennis Lehane","","The Drop",null))